---if语句， lua不支持switch, 所以一连串的if elseif是很常见的
local a = -10;
if a < 0 then
    a = 0
end
print(a)
a = a < 0 and 0 or a--等价于
print(a)
--elseif else的使用
local x, y, z = 10, 20
local cht = "%"
if cht == "+" then
    z = x + y
elseif cht == "-" then
    z = x - y
else
    z = x * y
end
print(z)

tt = { 1, 2, 3 }
---while
print("----------while--------")
local i = 1
while tt[i] do
    print(tt[i])
    i = i + 1
end

---repeat, 先执行一次， until为true的时候会结束
print("----------repeat--------")
local j = 1
repeat
    print(tt[j])
    j = j + 1
    local rrrr = tt[j]
until rrrr == nil--until的作用于可以访问到块里面的local变量

---for, 如果不指定, 默认步长是1
print("-----for-------", math.huge)
for i = 1, 10 do
    print(i)
end
local dd = nil
for i = 10, 1, -1 do
    print(i)
    if i >= 10 then
        dd = i
    end
end
print(dd)

---for in
print("-----for in-------")
local ttt = {"sunday", "monday", "tuesday", "wednesday", "thursday", "friday", "saturday"}
local rev_ttt = {}
for key, value in ipairs(ttt) do
    print(key, value)
    rev_ttt[value] = key
end
print(rev_ttt["sunday"])

