function aa(...)
    local s = 0
    for i, v in ipairs({ ... }) do
        --注意，这里的...是被{}包裹的
        s = s + v
    end
    return s
end
print(aa(1, 2, 3))--这里传入的不是数组，就是多个参数
print(aa(table.unpack({ 1, 2, 3 })))--使用unpack来组装一个多变参数

function aa1(...)
    local a, b, c = ...--用一个local多变量赋值来表示的
    print(a, b, c)
    return ...--直接返回...
end
print(aa1(1, 2, 3))

---使用select获取所有可变参数， 参数可以包含nil
function aa2(...)
    for i = 1, select('#', ...) do
        local arg = select(i, ...)--select返回下边从i开始后面的所有的值，因为这里只有1个变量做接收，所以只有1个有效
        print(arg)
    end
end
aa2(10, nil, 3)

