---协程---

local co = coroutine.create(function()--创建一个协程， 传入一段执行函数
    for i = 1, 10 do
        print(i)
    end
end)
print(co)
print(coroutine.status(co))--查看某个协程的状态， 创建完是suspended挂起状态
coroutine.resume(co)--执行/唤醒一个协程，挂起->运行
print(coroutine.status(co))--resume了以后变成死亡状态
coroutine.resume(co)--死亡状态再运行